mapper.update:
update model
set model_name = #{modelName,jdbcType=VARCHAR},
create_user_id = #{createUserId,jdbcType=INTEGER},
createtime = #{createtime,jdbcType=TIMESTAMP}
where model_id = #{modelId,jdbcType=INTEGER}

mapper.select:
select

from model where create_user_id=#{model.createUserId} or create_user_id=2017
order by createtime desc LIMIT #{start},#{count}

mapper.update:
update model
set type = 1
where model_id = #{modelId,jdbcType=INTEGER}

mapper.select:
select

from model where type=1 and create_user_id=#{model.createUserId}
order by createtime desc LIMIT #{start},#{count}
